package TaskOops;

public class Addition extends Calculator

{

	@Override
	public int addition(int a, int b) {
		// TODO Auto-generated method stub
		return a+b;
	}
	
	public int addition(int a, int b,int c) {
		// TODO Auto-generated method stub
		return a+b+c;
	}
	public int addition(int a, int b, int c, int d) {
		// TODO Auto-generated method stub
		return a+b+c+d;
	}

	
}
