package TaskOops;

public class Substraction extends Calculator
{

	@Override
	public int substraction(int a, int b) {
		// TODO Auto-generated method stub
		return a-b;
	}
	
	public int substraction(int a, int b,int c) {
		// TODO Auto-generated method stub
		return a-b-c;
	}
	
	public int substraction(int a, int b, int c , int d) {
		// TODO Auto-generated method stub
		return a-b-c-d;
	}

}
